import React from 'react';
import Header from './components/header'
import Image from './components/image'
import logo from './img/logo.png'


class App extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            helptext:'Help Text',
            userDate: ''
        }
        this.inputClick = this.inputClick.bind(this)
    }
    // helptext = "Help Text"

    render() {
    return( 
    <div>
        <Header name="Шапка профиля"/>
        <Header title="Шапка профиля!!!"/>
        <Header title="!!!"/>

        <h1 onClick={this.inputClick} >{this.state.helptext}</h1>
        <h1 onClick={this.inputClick} onMouseEnter={this.mouseOver}>{this.state.helptext}</h1>
        <Image image={logo}/>
        <img src = {logo}/>
        <img src='/home/maks/Desktop/react_2/my_app/src/img/logo.png'/>  {/*такой импорт картинки не работает*/}
    </div>
    )    
    }
    inputClick() {
        this.setState({helptext:'chenged'})
        console.log('Clicked')}
    mouseOver() {console.log('Mouse Over')}

}

export default App
